mapper.update:
update user set deleted = 1

    id in

        #{id}

mapper.select:
select user.id as id,
user.name as name,
user.username as username,
info.gender as gender,
info.email as email,
info.phone as phone
from user
join info on user.id = info.user_id

    user.deleted = 0
    and user.username = #{request.username}
    and user.name = #{request.name}
    and info.gender = #{request.gender}
    and info.email = #{request.email}
    and info.phone = #{request.phone}
